<template>
	<view class="content" style="position: relative;">
		<view class="btn_add" @click="addGift()">
			+ 添加新的礼品
		</view>
		<view v-if="list.length == 0" class="no_list">
			<view class="bg"></view>
			<view class="top_text">您还没有登记礼品，先添加⼀下吧～</view>
		</view>
		<view v-else class="project_box">
			<uni-row>
				<uni-col :xs="12" v-for="(item,index) in list" :key="index">
						<view class="card" @click="gotoDatails(item)">
							<image :src="item.pics" class="item_img"></image>
							<view class="card_body">
								<view class="card_title">
									{{item.name}}
								</view>
								<view class="card_btn">
									<view class="btn-error" @click.stop="addTogift(item)">添加到礼包</view>
								</view>
							</view>
						</view>
				</uni-col>
			</uni-row>
		</view>
		<LoadingMore v-if="list.length > 5" :lading_text="loadStatus"></LoadingMore>
	</view>
</template>

<script>
	import LoadingMore from '@/components/loadingmore/loadingmore.vue'
	export default {
		components:{LoadingMore},
		data() {
			return {
				loadStatus: 'loadmore',
				page_size:6,
				list:[
					
				]
			}
		},
		onShow() {
			this.getData()
		},
		methods: {
			addGift(){
				uni.navigateTo({
					url:"/pages/mine/my_gift/add_gift/add_gift"
				})
			},
			gotoDatails(e){
				uni.navigateTo({
					url:"/pages/mine/my_gift/details/details?id=" + e.id
				})
			},
			onReachBottom() {
				this.loadStatus = 'loading';
				this.page_size += 6
				// 模拟数据加载
				setTimeout(() => {
					this.getData()
					this.loadStatus = 'loadmore';
				}, 1000)
			},
			getData(){
				this.$request(1016, {
					curr_page: 1,
					page_size: this.page_size
				}).then(res => {
					this.list = res.data.data_list
					// res.data.data_list.map(item=>{
					//  this.list.push(item)
					// })
				
				})
			},
			addTogift(item) {
				const card_id = uni.getStorageSync('card_id')
				if(!card_id){
					uni.showToast({
						title:'请先选择贺卡，在添加礼品！',
						icon:'none'
					})
				}else{
						this.$request(1026, {
							gid: item.id
						}).then(res => {
							if (res.code == 0) {
								uni.showToast({
									title: '添加到礼包'
								})
								setTimeout(() => {
									uni.switchTab({
										url: '/pages/gift_bag/gift_bag'
									})
								}, 1500)
							}
						})
				}
			},
		}
	}
</script>

